import React, { useEffect, useState, useMemo } from "react";
import { useSelector, useDispatch } from "react-redux";
import Router, { useRouter } from "next/router";
import PropTypes from "prop-types";
import throttle from "lodash.throttle";
import cn from "classnames";
import Link from "next/link";
import Button from "@components/buttons/button";
import SmallPhotoWithText from "@components/small-photo-with-text";
import { getUser } from "@selectors/user.selectors";
import { getChainId } from "@selectors/global.selectors";
import { openConnectMetamaskModal } from "@actions/modals.actions";
import accountActions from "@actions/user.actions";
import {
  getEnabledNetworkByChainId,
  requestSwitchNetwork,
} from "@services/network.service";

import Logo from "./logo";
import styles from "./styles.module.scss";

const HeaderTopLine = ({ className, isShowStaking, buttonText, linkText }) => {
  const [hasScrolled, setHasScrolled] = useState(false);
  const [isCollapse, setIsCollapse] = useState(false);

  useEffect(() => {
    const handleScroll = throttle(() => {
      const offset = 0;
      const { scrollTop } = document.documentElement;
      const scrolled = scrollTop > offset;

      if (hasScrolled !== scrolled) {
        setHasScrolled(scrolled);
      }
    }, 200);

    document.addEventListener("scroll", handleScroll);
    return () => {
      document.removeEventListener("scroll", handleScroll);
    };
  }, [hasScrolled]);

  const dispatch = useDispatch();
  const user = useSelector(getUser);
  const chainId = useSelector(getChainId);
  const [hamburger, setHamburger] = useState(false);
  const network = useMemo(() => {
    return getEnabledNetworkByChainId(chainId);
  }, [chainId]);

  const router = useRouter();
  const pathname = router.pathname;

  const isOnRightNetwork =
    pathname !== "/bridge" &&
    pathname !== "/bridge/deposit" &&
    !pathname.includes("withdraw/pending");

  const wrongNetworkText =
    pathname !== "/bridge" && pathname !== "/bridge/deposit"
      ? network?.alias !== "matic"
        ? "Please switch to Matic Network"
        : ""
      : "Please switch to Mainnet";

  const switchNetwork = async () => {
    const res = await requestSwitchNetwork();
    return res;
  };

  if (!user) {
    dispatch(accountActions.checkStorageAuth());
  }

  const handleClick = () => dispatch(openConnectMetamaskModal());
  const onIconHander = () => {
    setIsCollapse(!isCollapse);
  };

  const [isShowMenu, setIsShowMenu] = useState(false);

  const handleProfileClick = () => {
    setIsShowMenu(false);
    Router.push("/profile").then(() => window.scrollTo(0, 0));
  };
  const handleLogoutClick = () => {
    setIsShowMenu(false);
    dispatch(accountActions.logout());
  };
  const handleManageInventory = () => {
    setIsShowMenu(false);
    Router.push("/manage-inventory").then(() => window.scrollTo(0, 0));
  };

  return (
    <div
      className={cn(
        className,
        styles.wrapper,
        hasScrolled ? styles.floatingNav : "",
        pathname.includes("bridge") ? styles.bridgeHeader : ""
      )}
    >
      {!isOnRightNetwork && (
        <p className={styles.notification}>{wrongNetworkText}</p>
      )}
      <div className={styles.leftBox}>
        <Logo black />
      </div>
      <div className={styles.rightBox}>
        <div
          className={cn(styles.links, isCollapse ? styles.expandedMenu : "")}
        >
          <Link href="">
            <a className={styles.link} target="_blank">
              DOCUMENTATION
            </a>
          </Link>
          <Link href="">
            <a className={styles.link} target="_blank">
              Web3 Fashion manifesto
            </a>
          </Link>
          <Link href="">
            <a className={styles.link}>Sub-thread weekly</a>
          </Link>
          <Link href="">
            <a className={styles.link} target="_blank">
              young letters to a vc
            </a>
          </Link>
          <Link href="">
            <a className={styles.link} target="_blank">
              MATIC-ETH BRIDGE
            </a>
          </Link>
        </div>
        {/* {network?.alias !== "matic" ? (
          <Button
            onClick={() => switchNetwork()}
            className={cn(
              styles.switchNetwork,
              isCollapse ? styles.expandedMenu : ""
            )}
          >
            Switch Network
          </Button>
        ) : null} */}
        <div className={styles.signBtn}>
          {user ? (
            <div
              className={styles.buttonWrapper}
              onClick={() => setIsShowMenu(!isShowMenu)}
            >
              <SmallPhotoWithText
                photo={
                  user.get("avatar")
                    ? user.get("avatar")
                    : "./images/user-profile/user-avatar-black.png"
                }
                address={user.get("username")}
                className={styles.hashAddress}
              >
                <button className={styles.arrowBottom}>
                  <img
                    className={styles.arrowBottomImg}
                    src="./images/icons/arrow-bottom.svg"
                    alt="arrow-bottom"
                  />
                </button>
              </SmallPhotoWithText>
              {isShowMenu && (
                <div className={styles.menuWrapper}>
                  <button
                    onClick={() => handleProfileClick()}
                    className={styles.menuButton}
                  >
                    Profile
                  </button>
                  <button
                    onClick={() => handleManageInventory()}
                    className={styles.menuButton}
                  >
                    Manage Inventory
                  </button>
                  <button
                    onClick={() => handleLogoutClick()}
                    className={styles.menuButton}
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Button
              className={styles.signInButton}
              onClick={() => handleClick()}
            >
              {buttonText}
            </Button>
          )}
        </div>
        <a className={styles.collapseIcon} onClick={onIconHander}>
          &#9776;
        </a>
      </div>
    </div>
  );
};

HeaderTopLine.propTypes = {
  className: PropTypes.string,
  isShowStaking: PropTypes.bool,
  buttonText: PropTypes.string,
  linkText: PropTypes.string,
};

HeaderTopLine.defaultProps = {
  className: "",
  isShowStaking: false,
  buttonText: "SIGN IN",
  linkText: "Staking",
};

export default HeaderTopLine;
